mapper.select:
SELECT count(*)
FROM COUNSELING
WHERE MEM_ID = #{memId}
AND COUNSEL_REQ_DATETIME = #{counselReqDatetime}
AND COUNSEL_STATUS IN ('S04001', 'S04003')

SELECT count(*)
    FROM COUNSEL
    WHERE COUNSEL = #{counsel}
    AND COUNSEL_REQ_DATETIME = #{counselReqDatetime}
    AND COUNSEL_STATUS IN ('S04001', 'S04003')
