mapper.update:
MERGE INTO COUNSELING_LOG CL
USING DUAL ON(
 CL.CL_IDX = #{clIdx}
)
WHEN MATCHED THEN
    UPDATE
    SET CL_CONTENT = #{clContent}
    , CL_DIFFICULTY = #{clDifficulty}
    , CL_CONTINUE = #{clContinue}
    , CL_CONFIRM = #{clConfirm}
    , UPDATED_AT = SYSDATE
    , FILE_GROUP_ID = #{fileGroupId}
WHEN NOT MATCHED THEN
    INSERT (
	    CL_IDX
		, COUNSEL_ID
		, CL_CONTENT
		, CL_DIFFICULTY
		, CL_CONTINUE
		, CL_CONFIRM
		, CREATED_AT
		, UPDATED_AT
		, FILE_GROUP_ID
    )VALUES(
        (SELECT NVL(MAX(CL_IDX),0)+1 FROM COUNSELING_LOG)
        , #{counselId}
        , #{clContent}
        , #{clDifficulty}
        , #{clContinue}
        , #{clConfirm}
        , SYSDATE
        , SYSDATE
        , #{fileGroupId}
    )
